tool.command:
for infile in `echo $infiles | perl -pe 's/\,/\n/g'`; do echo \$infile >> /tmp/infiles.txt; done;
for infile in `echo $infiles.names | perl -pe 's/\,/\n/g'`; do echo \$infile >> /tmp/infiles.name.txt; done;
export inlength=`cat /tmp/infiles.txt | wc -l`;
for c in `seq 1 \$inlength`; do
    export infile=`cat /tmp/infiles.txt | head -n \$c | tail -n 1`;
    export indir=`dirname \$infile`;
    export cpfile=`cat /tmp/infiles.name.txt | head -n \$c | tail -n 1`;
    export samplename=`basename \$cpfile .zip`;
    echo "seq: \$c";
    echo "vendor: $venformat";
    echo "infile: \$infile";
    echo "samplename: \$samplename";
    cp -f \$infile /tmp/\$cpfile;
    mkdir -p /tmp/\$samplename;
    unzip -q -d /tmp/\$samplename /tmp/\$cpfile;
    export acqusfile=`find /tmp/\$samplename -name "acqus" -print`;
    echo "acquisitionfile: \$acqusfile";
    mv `dirname \$acqusfile`/* /tmp/\$samplename/;
    java -jar /usr/local/share/nmrML/converter.jar -l create -b -z -t $venformat -a \$samplename -i /tmp/\$samplename -o $outfiles;
done;
